import React, { Suspense, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Route, Routes } from "react-router";
import "./App.css";
import Header from "./components/Header/Header";

import Alert from "./components/UI/alert/alert";
import Loading from "./components/UI/loading/loading";
const Main = React.lazy(() => import("./components/mainPage/main"));
const Favorites = React.lazy(() => import("./components/favorites/favorites"));

function App() {
  const dispach = useDispatch();
  const AlertText = useSelector((state) => {
    return state.alert;
  });
  useEffect(() => {
    navigator.geolocation.getCurrentPosition((pos) => {
      fetch(
        `http://dataservice.accuweather.com/locations/v1/cities/geoposition/search?apikey=tZue34nmLbdXAXJG9XMDx1AV7Za3rUoR&q=${pos.coords.latitude}%2C${pos.coords.longitude}`
      )
        .then((res) => {
          return res.json();
        })
        .then((res) => {
          dispach({
            type: "changeLocation",
            payload: {
              location: res.AdministrativeArea.LocalizedName,
              key: res.Key,
            },
          });
        })
        .catch((err) => {
          dispach({
            type: "alert",
            payload: { text: "oops couldn't find your location" },
          });
        });
    });
  }, []);
  return (
    <div className="App">
      <Header />
      <Routes>
        <Route
          path="/"
          element={
            <Suspense fallback={<Loading />}>
              <Main />
            </Suspense>
          }
        />
        <Route
          path="favorites"
          element={
            <Suspense fallback={<Loading />}>
              <Favorites />
            </Suspense>
          }
        ></Route>
      </Routes>
      {AlertText !== "" && <Alert />}
    </div>
  );
}

export default App;
