import Classes from "./currentweather.module.css";
import FavoritesButton from "../../UI/favoritesButton/favoritesButton";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
const Currentweather = (props) => {
  const dispach = useDispatch();
  const [currentConditions, setCurrentConditions] = useState({});
  const location = useSelector((state) => {
    return state.location;
  });
  console.log(location);
  useEffect(() => {
    fetch(
      `http://dataservice.accuweather.com/currentconditions/v1/${location.key}?apikey=vHpVFJikTtHFfeWGvUo8EqmhA5ZX7NAA`
    )
      .then((res) => {
        return res.json();
      })
      .then((res) => {
        console.log(res);
        let icon = res[0].WeatherIcon;
        if (icon < 10) {
          icon = "0" + icon;
        }
        let requiredDetails = {
          temperature: res[0].Temperature.Metric.Value,
          WeatherText: res[0].WeatherText,
          icon: icon,
          city: location.city,
          key: location.key,
        };
        console.log(requiredDetails);

        setCurrentConditions(requiredDetails);
      });
    // dont delete
  }, [location]);

  return (
    <div className={Classes.currentweather}>
      <FavoritesButton
        id={currentConditions.key}
        city={currentConditions.city}
      />
      <div className={Classes.NextWeek}></div>
      <h1>{currentConditions.city}</h1>
      <div className={Classes.temperature}>
        {currentConditions.temperature}&#8451;
      </div>
      <img
        src={`https://developer.accuweather.com/sites/default/files/${currentConditions.icon}-s.png`}
        loading="lazy"
        alt="icon"
      ></img>
      <h2>{currentConditions.WeatherText}</h2>
    </div>
  );
};

export default Currentweather;
