import Classes from "./dropDown.module.css";
import { useEffect, useState } from "react";
const DropDown = (props) => {
  const [autoCompleteLists, setAutoCompleteLists] = useState([]);
  const data = [
    {
      LocalizedName: "San",
      key: 1,
    },
    {
      LocalizedName: "Sa",
      key: 3,
    },
    {
      LocalizedName: "Sans",
      key: 4,
    },
    {
      LocalizedName: "Sang",
      key: 8,
    },
    {
      LocalizedName: "Sant",
      key: 7,
    },
  ];
  useEffect(() => {
    if (props.inputVal !== "") {
      fetch(
        `http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=vHpVFJikTtHFfeWGvUo8EqmhA5ZX7NAA&q=${props.inputVal}`
      )
        .then((res) => {
          return res.json();
        })
        .then((res) => {
          console.log(res);
          let arr = [];
          res.forEach((city) => {
            arr.push({ location: city.LocalizedName, key: city.key });
          });
          setAutoCompleteLists(arr);
        });
      // let arr = [];
      // data.forEach((city) => {
      //   arr.push({ location: city.LocalizedName, key: city.key });
      // });
      // setAutoCompleteLists(arr);
      // console.log(arr);
    }
  }, [props.inputVal]);

  const autoComplete = (event) => {
    console.log(event.currentTarget.value);
    props.autoComplete(autoCompleteLists[event.currentTarget.id]);
  };
  return (
    <div className={Classes.dropDown}>
      {autoCompleteLists.map((city, index) => {
        return (
          <div id={index} onClick={autoComplete}>
            {city.location}
          </div>
        );
      })}
    </div>
  );
};

export default DropDown;
