import Classes from "./currentweather.module.css";
import FavoritesButton from "../../UI/favoritesButton/favoritesButton";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
const Currentweather = (props) => {
  const [currentConditions, setCurrentConditions] = useState({});
  const loaction = useSelector((state) => {
    return state.loaction;
  });
  useEffect(() => {
    fetch(
      `http://dataservice.accuweather.com/currentconditions/v1/${loaction.key}?apikey=vHpVFJikTtHFfeWGvUo8EqmhA5ZX7NAA`
    )
      .then((res) => {
        return res.json();
      })
      .then((res) => {
        let requiredDetails = {
          temperature: res.Temperature.Metric.Value,
          WeatherText: res.WeatherText,
          icon: res.WeatherIcon,
        };
        setCurrentConditions(requiredDetails);
      });
  }, []);

  return (
    <div class={Classes.currentweather}>
      <FavoritesButton id="5" />
      <div className={Classes.NextWeek}></div>
      <h1>{props.city}</h1>
      <div className={Classes.temperature}>{props.temperature}&#8451;</div>
      <img
        src={`https://developer.accuweather.com/sites/default/files/${props.icon}-s.png`}
        loading="lazy"
        alt="icon"
      ></img>
      <h2>{props.WeatherText}</h2>
    </div>
  );
};

export default Currentweather;

Currentweather.defaultProps = {
  city: "Tel Aviv",
  temperature: "30",
  WeatherText: "Cloudy",
  icon: "04",
};
