const initailState = {
  favorites: {},
};
console.log(initailState);
const Reducer = (state = initailState, action) => {
  console.log(action);
  let temp;
  switch (action.type) {
    case "add":
      temp = state.favorites;
      temp[action.payload.id] = true;
      console.log(temp);
      return { favorites: temp };
    case "remove":
      temp = state.favorites;
      console.log(temp);
      temp[action.payload.id] = false;
      console.log(temp);
      return temp;
    default:
      return state;
  }
};
export default Reducer;
