import Classes from "./dropDown.module.css";
import { useEffect, useState } from "react";
const DropDown = (props) => {
  const [autoCompleteLists, setAutoCompleteLists] = useState();
  const data = [
    {
      LocalizedName: "San",
    },
    {
      LocalizedName: "Sa",
    },
    {
      LocalizedName: "Sans",
    },
    {
      LocalizedName: "Sang",
    },
    {
      LocalizedName: "Sant",
    },
  ];
  useEffect(() => {
    if (props.inputVal !== "") {
      // fetch(
      //   `http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=vHpVFJikTtHFfeWGvUo8EqmhA5ZX7NAA&q=${props.inputVal}`
      // )
      //   .then((res) => {
      //     return res.json();
      //   })
      //   .then((res) => {
      //     console.log(res);
      //   });
      let arr = [];
      data.forEach((city) => {
        arr.push(city.LocalizedName);
      });
      console.log(arr);
    }
  }, [props.inputVal]);

  const autoComplete = (event) => {
    props.autoComplete(event.currentTarget.id);
  };
  return (
    <div className={Classes.dropDown}>
      {autoCompleteLists.map((city) => {
        return (
          <div id={city} onClick={autoComplete}>
            {city}
          </div>
        );
      })}
    </div>
  );
};

export default DropDown;

DropDown.defaultProps = {
  Cities: ["tel Aviv", "teheran", "ror", "hjdf", "dkfdj"],
};
