import Classes from "./dropDown.module.css";
import { useEffect, useState } from "react";
const DropDown = (props) => {
  const [autoCompleteLists, setAutoCompleteLists] = useState([]);
  // useEffect(() => {
  //   if (props.inputVal !== "" && props.inputVal.length > 1) {
  //     fetch(
  //       `http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=tZue34nmLbdXAXJG9XMDx1AV7Za3rUoR&q=${props.inputVal}`
  //     )
