const initailState = {
  favorites: {},
  location: { city: "Tel aviv", key: 215854 },
  searchedLocations: {},
};
console.log(initailState);
const Reducer = (state = initailState, action) => {
  console.log(state, action);
  let temp;
  switch (action.type) {
    case "addFavorite":
      temp = state.favorites;
      temp[action.payload.id] = action.payload.city;
      console.log(temp);
      return { favorites: temp, location: state.location };
    case "removeFavorite":
      temp = state.favorites;
      delete temp[action.payload.id];
      console.log(temp);
      return { favorites: temp, location: state.location };
    case "changeLocation":
      return {
        favorites: state.favorites,
        location: {
          city: action.payload.location,
          key: action.payload.key,
        },
      };
    case "AddCurrentWheather":
      temp = state.searchedLocations;
      temp[action.payload.key].current = action.payload.current;
      return {
        favorites: state.favorites,
        location: state.location,
        searchedLocations: temp,
      };
    default:
      return state;
  }
};
export default Reducer;
