import Card from "../UI/Card/card";
import Currentweather from "./currentweather/currentweather";
import NextWeekWeather from "./nextWeekWeather/nextWeekWeather";
import SearchBar from "./searchBar/SearchBar";
import Classes from "./main.module.css";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

const Main = (props) => {
  const dispach = useDispatch();
  const location = useSelector((state) => {
    return state.location;
  });
  useEffect(() => {
    let requiredDetails = {};
    // fetch(
    //   `http://dataservice.accuweather.com/currentconditions/v1/${loaction.key}?apikey=vHpVFJikTtHFfeWGvUo8EqmhA5ZX7NAA`
    // )
    //   .then((res) => {
    //     return res.json();
    //   })
    //   .then((res) => {
    //     console.log(res);
    //     let requiredDetails = {
    //       temperature: res[0].Temperature.Metric.Value,
    //       WeatherText: res[0].WeatherText,
    //       icon: res[0].WeatherIcon,
    //     };
    //     console.log(requiredDetails);
    //     setCurrentConditions(requiredDetails);
    //   });
    let current = {
      temperature: 20,
      WeatherText: "sunny",
      city: location.city,
      key: location.key,
    };
    // dont delete
    let icon = 7;
    if (icon < 10) {
      requiredDetails.icon = "0" + icon;
    } else {
      requiredDetails.icon = icon;
    }
    requiredDetails.current = current;

    fetch(
      `http://dataservice.accuweather.com/forecasts/v1/daily/5day/${location.key}?apikey=vHpVFJikTtHFfeWGvUo8EqmhA5ZX7NAA`
    )
      .then((res) => {
        return res.json();
      })
      .then((res) => {
        console.log(res);
        let future = [];
        let options = {
          weekday: "long",
          year: "numeric",
          month: "long",
          day: "numeric",
        };
        res.forEach((day) => {
          future.push({
            day: day.Date.toLocaleDateString("en-US", options),
            min: day.Temperature.Minimum.Value,
            max: day.Temperature.Maximum.Value,
            src: "04",
          });
        });
        console.log();
      });
    dispach({
      type: "AddCurrentWheather",
      payload: { key: location.key, current: requiredDetails },
    });
  }, [location]);

  return (
    <main className={Classes.mainPage}>
      <SearchBar />
      <Card class={Classes.mainCard}>
        <NextWeekWeather />
        <Currentweather />
      </Card>
    </main>
  );
};

export default Main;
