import { useEffect, useState } from "react";
import Card from "../../UI/Card/card";
import Classes from "./FavoritesCard.module.css";
const FavoritesCard = (props) => {
  const [data, setData] = useState();
  useEffect(() => {
    fetch(
      `http://dataservice.accuweather.com/currentconditions/v1/${props.key}?apikey=vHpVFJikTtHFfeWGvUo8EqmhA5ZX7NAA`
    )
      .then((res) => {
        console.log(3);
        return res.json();
      })
      .then((res) => {
        console.log(res);
        let imgIcon = res.WeatherIcon;
        if (imgIcon < 10) {
          imgIcon = "0" + imgIcon;
        }
        setData({
          temperature: res.Temperature.Metric.Value,
          icon: imgIcon,
        });
      });
  }, [props]);
  return (
    <Card class={Classes.FavoritesCard}>
      <h2>{props.city}</h2>
      <img
        src={`https://developer.accuweather.com/sites/default/files/${data.icon}-s.png`}
        alt="Weather pic"
      ></img>
      <p>{data.temperature}&#8451;</p>
    </Card>
  );
};

export default FavoritesCard;
