import { useEffect, useState } from "react";
import DropDown from "./DropDown/dropDown";
import Classes from "./SerchBar.module.css";

const Search = (props) => {
  const [inputValue, setInputValue] = useState();

  useEffect(() => {
    console.log(
      `"http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=vHpVFJikTtHFfeWGvUo8EqmhA5ZX7NAA&q=${inputValue}`
    );
    // fetch(
    //   "http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=vHpVFJikTtHFfeWGvUo8EqmhA5ZX7NAA&q=" +
    //     inputHandler +
    //     ""
    // )
    //   .then((res) => {
    //     return res.json();
    //   })
    //   .then((res) => {
    //     console.log(res);
    //   });
  }, [inputValue]);

  const inputHandler = (event) => {
    console.log(event.target.value);
    setInputValue(event.target.value);
  };

  const autoCompleteHandler = (value) => {
    console.log(value);
    setInputValue(value);
  };
  return (
    <div className={Classes.searchContainer}>
      <form className={Classes.Search}>
        <button type="submit"></button>
        <input
          onChange={inputHandler}
          placeholder="Search here"
          value={inputValue}
          type="text"
        ></input>
      </form>
      {inputValue && <DropDown autoComplete={autoCompleteHandler} />}
    </div>
  );
};

export default Search;
